targets.queryText:
query {
  devices(name: "$device") {
    id
    name
    role {
      name
    }
    device_type {
      manufacturer {
        name
      }
      model
    }
    status {
      name
    }
  }
}

{
  interfaces(device: "$device", label__n: "loopback") {
    name
    label
    sot_device: device {
      name
    }
    sot_intended_state: status {
      name
    }
  }
}
